mapper.select:
SELECT A.*,B.* FROM ( SELECT TT1.rnum AS num, TT1.seq, TT1.title, TT1.read_cnt as "readCnt", TT1.div, TT1.mod_id as "modId", DECODE(TO_CHAR(SYSDATE, 'YYYYMMDD'),TO_CHAR(TT1.mod_dt,'YYYYMMDD') ,TO_CHAR(TT1.mod_dt,'HH24:MI') ,TO_CHAR(TT1.mod_dt,'YYYY-MM-DD')) AS "modDt" FROM( SELECT ROWNUM AS rnum, T1.* FROM ( SELECT * FROM pc_board --검색조건 WHERE seq > 0
ORDER BY mod_dt DESC ) T1 WHERE ROWNUM <= #{pageSize} * (#{pageNum} - 1) + #{pageSize} )TT1 WHERE rnum >= ( #{pageSize} * ( #{pageNum} - 1) +1) ) A CROSS JOIN (SELECT COUNT(*) AS totalCnt FROM pc_board --검색조건 WHERE seq > 0
) B

mapper.update:
UPDATE pc_board
   SET read_cnt = NVL(read_cnt,0) + 1
 WHERE seq = #{seq}

UPDATE pc_board
SET title = #{title}
    ,contents = #{contents}
    ,div = #{div}
    ,mod_dt = SYSDATE
    ,mod_id = #{modId}
WHERE seq = #{seq}
